        GROUP BY u.user_id
    )
    SELECT 
        um.*,
        RANK() OVER (ORDER BY trust_score DESC) as trust_rank,
        CASE 
            WHEN trust_score >= 80 THEN 'Highly Trusted'
            WHEN trust_score >= 60 THEN 'Trusted'
